mapper.update:
UPDATE anvd_order
SET	order_status = '주문취소'
WHERE order_num = ${orderNum}

mapper.insert:
	  
  	    SELECT odr_num_seq.currval
  	    FROM   dual
  	  
        INSERT INTO anvd_order (order_num
                               ,item_num
                               ,member_num
                               ,order_type
                               ,delivery_how
                               ,delivery_fee
                               ,delivery_address
                               ,payment_type)
        VALUES (odr_num_seq.nextval
               ,#{itemNum}
               ,#{memberNum}
               ,#{orderType}
               ,#{deliveryHow}
               ,#{deliveryFee}
               ,#{deliveryAddress}
               ,#{paymentType})
