mapper.update:
		UPDATE purchase

		SET purchase_ok = '구매확정'

		WHERE order_num = ${orderNum}

mapper.select:
SELECT order_num ,order_type ,order_status ,payment_type ,delivery_how ,item_num ,account_num ,seller_name ,seller_num ,member_num ,total ,delivery_address ,delivery_fee ,item_price ,order_date FROM vu_odr_com
seller_num = #{param2}
member_num = #{param1}
ORDER BY 1;
